import com.eviware.soapui.model.testsuite.TestRunner.Status;
import jxl.*;
def firstname, lastname, email;
def filepath = "C:\\Users\\RaghaviBanakalRavira\\Desktop\\SOAP assignment\\my-app\\testdata.xls"
File myfile = new File(filepath)
//this is to tell which workbook
Workbook wb = Workbook.getWorkbook(myfile)
//fetch data from sheet using sheet name
Sheet sh = wb.getSheet("Sheet1")
//log.info sh.getCell(1,1).getContents()
//identify no of rows and col
def row=sh.getRows().toInteger()
def column=sh.getColumns().toInteger()
log.info "The no of Rows are "+row
log.info "The no of Columns are "+column
//fetch the data
for ( i=0;i<row;i++)
{
	for( j=0;j<column;j++)
	{
		if (j==0)
		{
			firstname=sh.getCell(j,i).getContents()
		}
		else if(j==1)
		{
			lastname=sh.getCell(j,i).getContents()
		}
		else
		{
			email = sh.getCell(j,i).getContents()
		}		
	}
	log.info firstname
	log.info lastname
	log.info email

def tc = testRunner.testCase.testSuite.project.testSuites["Customer"].testCases["Customer Functional Testing"]
tc.testSteps["customerInfo"].setPropertyValue("firstname",firstname)
tc.testSteps["customerInfo"].setPropertyValue("lastname",lastname)
tc.testSteps["customerInfo"].setPropertyValue("email",email)
def runner = tc.run(null, false)
log.info "The status: $runner.status"
log.info "The time taken: $runner.timeTaken ms"
log.info "The Number of steps: "+runner.results.size()
}
